module AoC2015.Day01 ( output ) where

import System.IO
    ( stdin, hSetBuffering, BufferMode(LineBuffering) )

countFloors :: [Char] -> Int
countFloors [] = 0
countFloors (h : t)
  | h == '(' = countFloors t + 1
  | h == ')' = countFloors t - 1
  | otherwise = countFloors t

firstBasement :: [Char] -> Int
firstBasement list = firstBasementImpl list 1 0

firstBasementImpl :: [Char] -> Int -> Int -> Int
firstBasementImpl [] _ _ = -1
firstBasementImpl (h : t) count position = 
    let pos = position + (if h == '(' then 1 else -1) in
        if pos < 0 then count else firstBasementImpl t (count + 1) pos

